#include<stdio.h>
main(){int n,t,i,j,p1=0,p2=0,max,x;char a[60];scanf("%d",&t);int s[50]={0},p[50]={0},r[50]={0};scanf("%s",&a);scanf("%d",&n);char b[n][100];for(i=0;i<n;i++)scanf("%s",&b[i]);for(i=0;i<n;i++){for(j=0;j<t;j++)if(a[j]==b[i][j]){p1++;if(a[j]=='S')s[j]++;else if(a[j]=='P')p[j]++;else if(a[j]=='R')r[j]++;}else{if(a[j]=='S'){if( b[i][j]=='P'){p1+=2;    p[j]++;}else if(b[i][j]=='R')r[j]++;}else if(a[j]=='P'){if( b[i][j]=='S')s[j]++;else if(b[i][j]=='R'){p1+=2;r[j]++;}}else if(a[j]=='R'){if( b[i][j]=='S'){p1+=2;    s[j]++;}else if(b[i][j]=='P')p[j]++;}}}for(i=0;i<t;i++){max=0;x=(s[i]*1)+(p[i]*2);max=max>x?max:x;x=(p[i]*1)+(r[i]*2);max=max>x?max:x;x=(s[i]*2)+(r[i]*1);max=max>x?max:x;p2+=max;}printf("%d\n%d\n",p1,p2);}                           
